import Standings from "./standings"
import Matches from "./matches"
import { useState } from "react";
import { Routes } from "react-router-dom";
function View(){
    
    const [toggleMatches ,setToggleMatches] = useState(false); 
    const [toggleStandings , setToggleStandings] = useState(true); 



    function changeView(){
        setToggleStandings(!toggleStandings);
        setToggleMatches(!toggleMatches);

    }

    return (
        <>
        <div className="bg-white h-10 flex justify-center items-center">
            <button className="rounded-full bg-red-800 h-8 w-16 " onClick={()=>changeView()}>
                Matches
            </button>
            <button className="rounded-full bg-red-800 h-8 w-20 ml-6" onClick={()=>changeView()}>
                Standings
            </button>
        </div>
        <div>
            <Routes>
                <Route ></Route>
            </Routes>
            {toggleMatches && <Matches/>}
            {toggleStandings && <Standings/>}
        </div>
        
        </>
    )
}


export default View;
