import axios from "axios";
import React, { useEffect, useState } from "react";
import {Swiper,SwiperSlide} from "swiper/react";
import 'swiper/css';
import 'swiper/css/pagination';
import {Pagination} from "swiper/modules"

export default function News(){
    const [news,setNews] =useState([]);
    async function  getNews(){
        try{
            const response = await axios.get("https://newsapi.org/v2/top-headlines?category=sports&language=ar",{
                headers:{
                    "X-Api-Key":"b11bf6b87ba1448b801b135ee1384680"
                }
            })
            setNews(response.data.articles);
        }catch(err){
            console.log(err.message);
        }
    }
    useEffect(()=>{
        getNews();
    },[]);

    return(
        <Swiper
            style={{marginTop:"5px"}}
                slidesPerView={slidesPerView}
                spaceBetween={spaceBetween}
                pagination={{
                    clickable:true,
                }}
                modules={[Pagination]}
                className="mySwiper"
            >
                {news.length > 0 && news.map((element,index)=>{
                return <a href={element.url}><div key={index} className="bg-white h-24  flex-col justify-center items-center rounded-lg hover:bg-blue-200">
                           <h1 className="md:text-md text-lg font-bold">{element.title}</h1>
                </div>
                </a>
            })}
        </Swiper>
       
    )
}
