import axios from "axios";
import { useState ,useContext,useLayoutEffect, useRef } from "react";
import { AppContext } from "../App";

export default function Matches(){
    const [Matches,setMatches] = useState([]);
    const{state} = useContext(AppContext)
    const [LeagueCode,setLeagueCode] = useState(2014)
    const [response , setResponse] = useState(); 
    const [empty, setEmpty] = useState(false); 
    const refSelectedDay = useRef(null);

    useLayoutEffect(()=>{
        switch(true){
            case state.LaLIGA:
                setLeagueCode(2014);
                break
            case state.LIGUE_1:
                setLeagueCode(2015);
                break;
            case state.PREMIER_LEAGUE:
                setLeagueCode(2021);
                break;
            case state.SERIE_A:
                setLeagueCode(2019);
                break;
            case state.BUNDESLIGA:
                setLeagueCode(2002);
                break;
            default:
                return ; 
        }
    },[state])


    useLayoutEffect(()=>{
        getTeamsMatches();
    },[LeagueCode])
    
    const getTeamsMatches= async()=>{
        try{
            const response = await axios.get("https://api.football-data.org/v4/competitions/"+LeagueCode+"/matches",{
                headers:{"X-Auth-Token":"a4b408a61fec4bb99db984a6c7b67a50"}
            })
            
            const array = response.data.matches;

            const date = new Date();
            var day = date.getDate();
            var month = date.getMonth();
            var matchDay = 0 ; 

            for(var i = 0 ; i<(response.data.matches).length ; i++)
            {
                if(day >= getDayMatch((response.data.matches[i])) && month === getMonthMacth((response.data.matches[i]))){ 
                    matchDay = response.data.matches[i].matchday
                    break;
                }
                
            }
            if(matchDay === 0 ){
                matchDay = 1 ; 
            }
            var new_array = (array).filter((element)=>element.matchday === 1);
            if(refSelectedDay.current.value != null){
                getMonthMacth();
            }
            setMatches(new_array);
            setResponse(response)
            }catch(err){
                console.log(err.message);
            }
    }


    const getMonthMacth= ()=>{
        var date = "2023-07-14";
        return date.substr(5,2)
    }
    const getDayMatch= ()=>{
        var date = "2023-07-14";
        return date.substr(8,2)
    }
    function getDate(elementDay){
        var date = elementDay.substr(0,elementDay.indexOf("T"));
        var time = (elementDay.substr(elementDay.indexOf("T")+1)) ;
        time = time.substr(0,time.length -1); 
        return time+" / " + date ; 

    }


    function setMatchesByDate(date){
        let Matches = response.data.matches; 
        const array = [];
        Matches.forEach(element => {
           if(element.utcDate.substr(0,10) === date){
            array.push(element);
           }
        });

        if(array.length === 0 ){
            console.log("Array is empty ...")
            setEmpty(true);
            setMatches([]);
            return;
        }
        setEmpty(false);
        setMatches(array);
    }

    function setMatchesByToday(){
        var date = new Date();
        
        let Matches = response.data.matches; 
        const array = [] ; 
        Matches.forEach((element)=>{
            //2023-08-01
            let year = date.getFullYear();
            let month = date.getUTCMonth() +1 ;
            if(parseInt(month/10) === 0 ){
                month = "0"+month;
            } 
            let day = date.getUTCDate();
            
            const newTodayDate = year+"-"+month+"-"+day;
            if(element.utcDate.substr(0,10) === newTodayDate){
                array.push(element);
            }
        })

        if(array.length === 0){
            setEmpty(true);
            setMatches([]);
            return ; 
        }
        setEmpty(false);
        setMatches(array);
    }
    return(
        <div className="md:container h-full ">
            <h1 className="font-bold text-white w-full md:text-4xl flex py-10 px-2 underline"> Matches </h1>
            <div className="flex flex-row justify-between">
                <div>
                <button className="rounded-full bg-white h-10 w-20" onClick={()=>setMatchesByToday()}>Today</button>
                <button className="rounded-full bg-white h-10 w-20">Live</button>
                <button className="rounded-full bg-white h-10 w-20">Finished</button>
                </div>

                <input ref={refSelectedDay} type="date" className="rounded-full w-32" onChange={(event)=>setMatchesByDate(event.target.value)}/>
            </div>
            <div className=" grid grid-cols-1 gap-y-2  ">
            {Matches.length>0  &&
                Matches.map((element,index)=>{
                   return  <div key={index} className="container bg-black flex flex-col w-5/6 rounded-lg my-3 px-3 h-20 mx-auto">
                            <h1 className="text-center text-white">{getDate(element.utcDate)}</h1>
                            <div className="flex md:flex-row flex-col justify-between items-center md:text-3xl text-sm ">
                                <img src={element.homeTeam.crest} className="h-12 w-12" alt="" ></img>
                                <span className="text-lg text-white">{element.homeTeam.shortName}</span>
                                <div className="text-white">X</div>
                                <span className="text-lg text-white">{element.awayTeam.shortName}</span>
                                <img src={element.awayTeam.crest} className="h-12 w-12" alt=""></img>
                            </div>
                    </div>
                })
                
                }
            </div>
            {Matches.length===0 && !empty && 
                <div className="text-center text-4xl font-bold">
                    Loading ...</div>
                }
            {empty && 
                <div className="text-center text-4xl font-bold mt-8">
                    No Matches Available at this Date</div>
                }
        </div>
    )
}
