import axios from "axios";
import React, { useEffect, useState } from "react";
import {Swiper,SwiperSlide} from "swiper/react";
import 'swiper/css';
import 'swiper/css/pagination';

export default function News(){
    const [news,setNews] =useState([]);
    async function  getNews(){
        try{
            const response = await axios.get("https://newsapi.org/v2/top-headlines?category=sports&language=ar",{
                headers:{
                    "X-Api-Key":"b11bf6b87ba1448b801b135ee1384680"
                }
            })
            setNews(response.data.articles);
        }catch(err){
            console.log(err.message);
        }
    }
    useEffect(()=>{
        getNews();
    },[]);

    return(
        <Swiper className="container m-auto grid grid-cols-1 gap-y-2">
            {news.length > 0 && news.map((element,index)=>{
                return <a href={element.url}><SwiperSlide key={index} className="bg-white h-24  flex-col justify-center items-center rounded-lg hover:bg-blue-200">
                           <h1 className="md:text-md text-lg font-bold">{element.title}</h1>
                </SwiperSlide>
                </a>
            })}

        </Swiper>
    )
}
