import Standings from "./standings"
import Matches from "./matches"
import { useState } from "react";
function View(){
    const state = use
    const [toggleMatches ,setToggleMatches] = useState(false); 
    const [toggleStandings , setToggleStandings] = useState(true); 
    const [LeagueCode,setLeagueCode] = useState(2014);


    function changeToMatches(){
        
        setToggleStandings(false);
        setToggleMatches(true);

    }
    function changeToStandings(){
        
        setToggleStandings(true);
        setToggleMatches(false);

    }
    
    useLayoutEffect(()=>{
        switch(true){
            case state.LaLIGA:
                setLeagueCode(2014);
                break
            case state.LIGUE_1:
                setLeagueCode(2015);
                break;
            case state.PREMIER_LEAGUE:
                setLeagueCode(2021);
                break;
            case state.SERIE_A:
                setLeagueCode(2019);
                 break;
            case state.BUNDESLIGA:
                setLeagueCode(2002);
                break;
            default:return;

        }
    },[state]) 


    return (
        <>
        <div className="bg-white h-10 flex justify-center items-center">
            <button className="rounded-full bg-red-800 h-8 w-16 " onClick={()=>changeToMatches()} code={LeagueCode}>
                Matches
            </button>
            <button className="rounded-full bg-red-800 h-8 w-20 ml-6" onClick={()=>changeToStandings()} code={LeagueCode}>
                Standings
            </button>
        </div>
        <div>
            {toggleMatches && <Matches/>}
            {toggleStandings && <Standings/>}
        </div>
        
        </>
    )
}


export default View;
