import React, { useLayoutEffect, useState } from "react";
import { ACTIONS } from "../App";
import axios from "axios";
function NavBar({dispatch}){
    var listOfExistingLeagues = [2002,2014,2015,2019,2021]
    const [leagues,setLeagues]=useState([]);

    useLayoutEffect(()=>{
        getLeagues();
    },[])

    const getLeagues=async ()=>{
        try{
            const response = await axios.get("https://api.football-data.org/v4/competitions",{
                headers:{
                    "X-Auth-Token":"a4b408a61fec4bb99db984a6c7b67a50"
                }
            })
            const array_of_accepted_leagues = []
            for(let i = 0 ; i< response.data.competitions.length ; i++){
                if( listOfExistingLeagues.indexOf(response.data.competitions[i].id) != -1){
                    array_of_accepted_leagues.push(response.data.competitions[i])
                }
            }
            setLeagues(array_of_accepted_leagues);
        }catch(err){
            console.log(err.message);
        }
    }





    return(
        <>
            <div className="bg-Color mx-2 rounded-xl navbar flex flex-col h-auto">
                <div className="mx-auto text-2xl text-white pt-2 text-Color">
                    LEAGUES
                </div>
                <ul className="flex flex-col ">
                    {leagues.map((element)=>{
                        return <li className="pt-4 md:text-sm text-white text-md hover:scale-110" onClick={()=>dispatch({type:element.name})}>{element.name}</li>
                    })}
                </ul>
            </div>
        </>
    );
}

export default NavBar
