import axios from "axios";
import { useState ,useContext,useLayoutEffect } from "react";
import { AppContext } from "../App";
export default function Matches(){
    const [Matches,setMatches] = useState([]);
    const{state} = useContext(AppContext)
    const [LeagueCode,setLeagueCode] = useState(2014)
    useLayoutEffect(()=>{
        switch(true){
            case state.LaLIGA:
                setLeagueCode(2014);
                break
            case state.LIGUE_1:
                setLeagueCode(2015);
                break;
            case state.PREMIER_LEAGUE:
                setLeagueCode(2021);
                break;
            case state.SERIE_A:
                setLeagueCode(2019);
                break;
            case state.BUNDESLIGA:
                setLeagueCode(2002);
                break;
            default:
                return ; 
        }
    },[state])


    useLayoutEffect(()=>{
        getTeamsMatches();
    })
    
    const getTeamsMatches= useCallBack()
    const getMonthMacth= ()=>{
        var date = "2023-07-14";
        return date.substr(5,2)
    }
    const getDayMatch= ()=>{
        var date = "2023-07-14";
        return date.substr(8,2)
    }
    function getDate(elementDay){
        var date = elementDay.substr(0,elementDay.indexOf("T"));
        var time = (elementDay.substr(elementDay.indexOf("T")+1)) ;
        time = time.substr(0,time.length -1); 
        return time+" / " + date ; 

    }

    return(
        <div className="md:container h-full ">
            <h1 className="font-bold text-white w-full md:text-4xl flex py-10 px-2 underline"> Matches </h1>
            <div className=" grid grid-cols-1 gap-y-2  ">
            {Matches.length>0 &&
                Matches.map((element,index)=>{
                   return  <div key={index} className="container bg-black flex flex-col w-5/6 rounded-lg my-3 px-3 h-20 mx-auto">
                            <h1 className="text-center text-white">{getDate(element.utcDate)}</h1>
                            <div className="flex md:flex-row flex-col justify-between items-center md:text-3xl text-sm ">
                                <img src={element.homeTeam.crest} className="h-12 w-12" alt="" ></img>
                                <span className="text-lg text-white">{element.homeTeam.shortName}</span>
                                <div className="text-white">X</div>
                                <span className="text-lg text-white">{element.awayTeam.shortName}</span>
                                <img src={element.awayTeam.crest} className="h-12 w-12" alt=""></img>
                            </div>
                    </div>
                })
                
                }
            </div>
            {Matches.length===0 && 
                <div className="text-center text-4xl font-bold">
                    Loading ...</div>
                }
        </div>
    )
}
